import UIKit
import Firebase
import FirebaseAuth
import FirebaseDatabase

class SearchViewController: UIViewController, UITableViewDataSource, UISearchBarDelegate, SearchCellDelegate, UITableViewDelegate {
    
    // MARK: - Outlets
    @IBOutlet var searchView: UIView!
    @IBOutlet weak var opportunityTableView: UITableView!
    @IBOutlet weak var searchBar: UISearchBar!
    
    // MARK: - Variables
    let userID = Auth.auth().currentUser?.uid
    let favoritesVC = FavoritesViewController(nibName: "FavoritesViewController", bundle: nil)
    var isLiked = false
    var ref: DatabaseReference! = Database.database().reference()
    var likedInternships = [Internship]()
    var likedScholarships = [Scholarship]()
    var internshipArray = [Internship]()
    var scholarshipArray = [Scholarship]()
    var searchInternship = [Internship]()
    var searchScholarship = [Scholarship]()
    var isSearching = false
    var buttonState = false
    
    override func viewDidLoad() {
        super.viewDidLoad()
        searchBar.delegate = self
        searchBar.layer.borderWidth = 1
        searchBar.layer.borderColor = Colors.linkedInBlue.cgColor
        setupInternships()
        setupScholarships()
    }
    
    override func viewWillAppear(_ animated: Bool) {
        super.viewDidAppear(animated)
    }
    
    func searchBar(_ searchBar: UISearchBar, selectedScopeButtonIndexDidChange selectedScope: Int) {
        opportunityTableView.reloadData()
    }
    
    func searchBar(_ searchBar: UISearchBar, textDidChange searchText: String) {
        if searchBar.selectedScopeButtonIndex == 0 {
            searchInternship = internshipArray.filter({$0.description.contains(searchText)})
            isSearching = true
            opportunityTableView.reloadData()
        } else {
            searchScholarship = scholarshipArray.filter({$0.description.contains(searchText)})
            isSearching = true
            opportunityTableView.reloadData()
        }
    }
    
    func searchBarCancelButtonClicked(_ searchBar: UISearchBar) {
        isSearching = false
        view.endEditing(true)
        opportunityTableView.reloadData()
    }
    
    func searchBarSearchButtonClicked(_ searchBar: UISearchBar) {
        view.endEditing(true)
        opportunityTableView.reloadData()
    }
    
    func likeButtonPressedForInternship(intershipVar: Internship) {
        intershipVar.isLiked = !intershipVar.isLiked
        if intershipVar.isLiked == true {
            if likedInternships.contains(where: {$0.title == intershipVar.title}) {
                let alert = UIAlertController(title: "Already in favorites!", message: nil, preferredStyle: .alert)
                let okButton = UIAlertAction(title: "OK", style: .default, handler: nil)
                alert.addAction(okButton)
                self.present(alert, animated: true, completion: nil)
            } else {
                let alert = UIAlertController(title: "Added to favorites! ", message: nil, preferredStyle: .alert)
                let okButton = UIAlertAction(title: "OK", style: .default, handler: nil)
                alert.addAction(okButton)
                self.present(alert, animated: true, completion: nil)
                likedInternships.append(intershipVar)
            }
        }
        let dict = intershipVar.convertToDict()
        ref?.child("Users").child(userID!).child("Internships").childByAutoId().updateChildValues(dict)
    }
    
    func likeButtonPressedForScholarship(scholarshipVar: Scholarship) {
        scholarshipVar.isLiked = !isLiked
        if scholarshipVar.isLiked == true {
            if likedScholarships.contains(where: {$0.title == scholarshipVar.title}) {
                let alert = UIAlertController(title: "Already in favorites!", message: nil, preferredStyle: .alert)
                let okButton = UIAlertAction(title: "OK", style: .default, handler: nil)
                alert.addAction(okButton)
                self.present(alert, animated: true, completion: nil)
            } else {
                let alert = UIAlertController(title: "Added to favorites!", message: nil, preferredStyle: .alert)
                let okButton = UIAlertAction(title: "OK", style: .default, handler: nil)
                alert.addAction(okButton)
                self.present(alert, animated: true, completion: nil)
                likedScholarships.append(scholarshipVar)
            }
        }
        let dict = scholarshipVar.convertToDict()
        ref?.child("Users").child(userID!).child("Scholarships").childByAutoId().updateChildValues(dict)
    }
    
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        
        if isSearching {
            if searchBar.selectedScopeButtonIndex == 0 {
                return searchInternship.count
            } else {
                return searchScholarship.count
            }
        } else {
            if searchBar.selectedScopeButtonIndex == 0 {
                return internshipArray.count
            } else {
                return scholarshipArray.count
            }
        }
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let internshipCell = tableView.dequeueReusableCell(withIdentifier: "internshipCell", for: indexPath) as! SearchViewControllerCell
        let scholarshipCell = tableView.dequeueReusableCell(withIdentifier: "scholarshipCell", for: indexPath) as! SearchViewControllerCell
        
        if isSearching {
            if searchBar.selectedScopeButtonIndex == 0 {
                internshipCell.title.text = searchInternship[indexPath.row].title
                internshipCell.title.lineBreakMode = .byTruncatingTail
                internshipCell.companyOrAmount.text = searchInternship[indexPath.row].company
                internshipCell.locationOrDeadline.text = searchInternship[indexPath.row].location
                internshipCell.delegate = self
                internshipCell.isScholarship = false
                internshipCell.internshipVar = searchInternship[indexPath.row]
                return internshipCell
            } else {
                scholarshipCell.title.text = searchScholarship[indexPath.row].title
                scholarshipCell.companyOrAmount.text = searchScholarship[indexPath.row].amount
                scholarshipCell.locationOrDeadline.text = searchScholarship[indexPath.row].deadline
                scholarshipCell.delegate = self
                scholarshipCell.isScholarship = true
                scholarshipCell.schoalrshipVar = searchScholarship[indexPath.row]
                return scholarshipCell
            }
        } else {
        if searchBar.selectedScopeButtonIndex == 0 {
            internshipCell.title.text = internshipArray[indexPath.row].title
            internshipCell.companyOrAmount.text = internshipArray[indexPath.row].company
            internshipCell.locationOrDeadline.text = internshipArray[indexPath.row].location
            internshipCell.delegate = self
            internshipCell.isScholarship = false
            internshipCell.internshipVar = internshipArray[indexPath.row]
            return internshipCell
        } else {
            scholarshipCell.title.text = scholarshipArray[indexPath.row].title
            scholarshipCell.companyOrAmount.text = scholarshipArray[indexPath.row].amount
            scholarshipCell.locationOrDeadline.text = scholarshipArray[indexPath.row].deadline
            scholarshipCell.delegate = self
            scholarshipCell.isScholarship = true
            scholarshipCell.schoalrshipVar = scholarshipArray[indexPath.row]
            return scholarshipCell
        }
    }
        
    }
    
    
    
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        let InternshipVC = storyboard?.instantiateViewController(withIdentifier: "InternshipViewController") as! InternshipDetailsViewController
        let ScholarshipsVC = storyboard?.instantiateViewController(withIdentifier: "ScholarshipViewController") as! ScholarshipsDetailsViewController
        
        if searchBar.selectedScopeButtonIndex == 0 && !searchInternship.isEmpty {
        InternshipVC.internshipName = searchInternship[indexPath.row].title
        InternshipVC.internshipCo = searchInternship[indexPath.row].company
        InternshipVC.internshipLoc = searchInternship[indexPath.row].location
        InternshipVC.internshipDesc = searchInternship[indexPath.row].description
        InternshipVC.internshipURL = searchInternship[indexPath.row].url
        tableView.deselectRow(at: indexPath, animated: true)
        self.navigationController?.pushViewController(InternshipVC, animated: true)
        } else if searchBar.selectedScopeButtonIndex == 1 && !searchScholarship.isEmpty {
            ScholarshipsVC.scholarshipName = searchScholarship[indexPath.row].title
            ScholarshipsVC.scholarshipAmnt = searchScholarship[indexPath.row].amount
            ScholarshipsVC.scholarshipDueDate = searchScholarship[indexPath.row].deadline
            ScholarshipsVC.scholarshipDesc = searchScholarship[indexPath.row].description
            ScholarshipsVC.scholarshipURL = searchScholarship[indexPath.row].url
            tableView.deselectRow(at: indexPath, animated: true)
            self.navigationController?.pushViewController(ScholarshipsVC, animated: true)
        } else if searchBar.selectedScopeButtonIndex == 0 {
            InternshipVC.internshipName = internshipArray[indexPath.row].title
            InternshipVC.internshipCo = internshipArray[indexPath.row].company
            InternshipVC.internshipLoc = internshipArray[indexPath.row].location
            InternshipVC.internshipDesc = internshipArray[indexPath.row].description
            InternshipVC.internshipURL = internshipArray[indexPath.row].url
            tableView.deselectRow(at: indexPath, animated: true)
            self.navigationController?.pushViewController(InternshipVC, animated: true)
        } else {
            ScholarshipsVC.scholarshipName = scholarshipArray[indexPath.row].title
            ScholarshipsVC.scholarshipAmnt = scholarshipArray[indexPath.row].amount
            ScholarshipsVC.scholarshipDueDate = scholarshipArray[indexPath.row].deadline
            ScholarshipsVC.scholarshipDesc = scholarshipArray[indexPath.row].description
            ScholarshipsVC.scholarshipURL = scholarshipArray[indexPath.row].url
            tableView.deselectRow(at: indexPath, animated: true)
            self.navigationController?.pushViewController(ScholarshipsVC, animated: true)
        }
        
        
    }
    
    // MARK: - Opportunities
    private func setupInternships() {
        internshipArray.append(Internship(title: "AAAS Minority Science Writers Internship", company: "AAAS", location: "New York", description: "The AAAS Minority Science Writers Internship places minority students interested in journalism as a career and who want to learn about science writing at Science Magazine for 10 weeks in the summer. Interns will cover the scientific and technological issues across the globe. This program is open to undergraduate minorities with a serious interest in science writing. Students pursuing a journalism major are given preference. Applicant must be in an academic program. Required materials: resume, unofficial transcripts, and two writing samples. One should be about a news event that took place after October 15. Application Essay: In less than 800 words, tell us about yourself and your interest in this internship: commitment to journalism, your thoughts about science and science writing, your career goals, and what you hope to get out of this internship.", url: "https://www.aaas.org/page/aaas-minority-science-writers-internship", isLiked: false))
        internshipArray.append(Internship(title: "Interpublic Group Scholarship and Internship", company: "Interpublic Group", location: "New York, NY", description: "The New York Women in Communications Foundation, in alliance with New York Women in Communications, awards the Interpublic Group Scholarship and Internship annually based on academic excellence, need and involvement in the field of communications. An award is given to an ethnically diverse student who is currently a college junior and has an interest advertising or public relations. Potential candidates must be able to fulfill a paid internship during the summer prior to the academic year the scholarship is offered. Eligibility: Must be a U.S. citizen or a permanent resident who live in NY, NJ, CT, or PA, college undergraduate and graduate students who are permanent residents of one of these states, be majoring or planning to declare a major or pursuing an advanced degree in advertising or public relations and have an overall GPA of 3.2+.", url: "https://scholarships.nywici.org/sponsored-scholarships", isLiked: false))
        internshipArray.append(Internship(title: "Smithsonian Institution James E. Webb Internship", company: "Smithsonian", location: "Washington, DC", description: "This program honors James. E. Webb, Regent Emeritus and former Administrator of the National Aeronautics and Space Administration, to promote excellence in the management of not-for-profit organizations. These opportunities are intended to increase participation of groups of the minority who are under-represented in the management of scientific and cultural organizations. Interns are situated in offices, museums, and research institutes throughout the Smithsonian Institution. Eligibility: Must be a U.S. citizens and U.S. permanent resident, must be enrolled in an undergraduate or in a graduate program of study in business or public administration. Students are expected to have a 3.0+ GPA", url: "https://www.smithsonianofi.com/internship-opportunities/james-e-webb-internship/", isLiked: false))
        internshipArray.append(Internship(title: "Tampa Bay Times Peterman Scholarship/News Internship", company: "Tampa Bay Times", location: "St. Petersburg, FL", description: "Only students who are hired as part of the Summer Internship Program at the Times are eligible to apply for the Tampa Bay Times Peterman Scholarship awards. Scholarships are worth up to $3,500 depending on your academic status. The Peggy Peterman Scholarship targets minority students in journalism and is worth $5,000 and includes a paid internship at the Times. Applicants must be full-time undergraduate or graduate minority students who are pursuing careers in journalism. Specify in your application whether you're applying for the Peterman Scholarship/News Internship.", url: "http://company.tampabay.com:2052/times-fund/scholarships", isLiked: false))
        internshipArray.append(Internship(title: "UNCF/Carnival Corporate Scholars Program", company: "Carnival Corp.", location: "Nationwide", description: "The UNCF/Carnival Corporate Scholars Program, sponsored in partnership with Carnival Corporation, is looking for college sophomores who are interested in pursuing a career in the hospitality industry and whose experience in their first two years of college demonstrates leadership and strategic and analytical ability. Candidates will be placed in a two-year scholarship and internship at various Carnival Corporation host sites. For the Minority sophomore undergraduate student enrolled full-time at a U.S. located, accredited four-year institution or a transfer student from a community college that has been accepted into an accredited four-year college or university, Community college students should select four-year under the enrollment status within the application, minimum GPA of 2.75; minimum GPA of 3.0 required for renewal, U.S. citizen or a permanent resident, majoring in hospitality management and/or administration, tourism, culinary arts, business, finance, communications, marketing or related field.", url: "https://scholarships.gmsp.org/Program/Details/39a4e769-abbf-467c-9af8-8f0163bfee37", isLiked: false))
        // MARK - 5
        internshipArray.append(Internship(title: "Bilingual Internship - PR & Influencer Marketing", company: "H+M Communications", location: "Los Angeles, CA", description: "H+M Communications is a full-service boutique and consumer-marketing consultancy with offices located in Los Angeles, New York, Miami and Seattle. H+M is dedicated to multicultural marketing with special focus on the U.S. Hispanic market and has become one of the top multicultural agencies trusted by many brands, film studios, television networks, content producers and distributors over the past fifteen years. H+M's expertise spans media and community relations, influencer outreach, grassroots/experiential marketing, and sponsorship development and activation. Some of our clients include: Universal Pictures, Telemundo, Fox Searchlight, Paramount, Comcast, Pantelion, STRONG by Zumba, and Sony Pictures Television among others.", url: "https://www.indeed.com/q-PR-Marketing-Internship-jobs.html?vjk=8a88bf619b55b7b2", isLiked: false))
        internshipArray.append(Internship(title: "CONGRESSIONAL INTERNSHIP PROGRAM", company: "Congressional Hispanic Caucus Institute", location: "Washington, D.C.", description: "CHCI Interns will meet accomplished leaders—including CHCI Alumni dedicated to improving the Hispanic/Latino community. These alumni are from a wide range of professional backgrounds, including Capitol Hill, Fortune 500 companies, NGOs, nonprofits, and many other organizations.", url: "https://chci.org/programs/congressional-internship-program/", isLiked: false))
        internshipArray.append(Internship(title: "Inroads", company: "INROADS", location: "Varies", description: "INROADS accepts applications year-round, however for priority consideration, it is recommended college undergraduates submit their online internship application by March 31st. The INROADS Application is the first step to becoming an INROADS Intern. Students applying to Canada or Mexico opportunities should email recruitment@inroads.org for unique application qualifications.", url: "https://inroads.org/internships/apply/", isLiked: false))
        internshipArray.append(Internship(title: "ACLU Immigrants' Rights Project Undergraduate Internship Program", company: "ACLU", location: "Varies", description: "The ACLU is an equal opportunity employer and values a diverse workforce.  The ACLU encourages applications from all qualified individuals without regard to race, color, religion, gender, sexual orientation, gender identity or expression, age, national origin, marital status, citizenship, disability, veteran status, and record of arrest or conviction. The ACLU uses affirmative action strategies in recruitment and employment efforts to assure that persons with disabilities have full opportunities for employment in all positions. Applicants are not required or expected to provide any current salary and compensation information, or salary history during any phase of the recruitment process.", url: "https://www.aclu.org/careers", isLiked: false))
        internshipArray.append(Internship(title: "American Economic Association Summer Minority Program", company: "American Economic Association", location: "Chicago, IL", description: "Since 1974, the AEA Summer Training Program and Scholarship Program have increased diversity in the field of economics by preparing talented undergraduates for doctoral programs in economics and related disciplines. AEASP is a opportunity that enables students to develop technical skills to prepare for their graduate studies. As many as 20% of PhDs awarded to minorities in economics over the past 20 years are graduates of this program. Students receive 2 months of intensive training in microeconomics, math, econometrics and research methods. At 3 credits per class, students have the opportunity to earn 12 college credits.", url: "https://scholarships.gmsp.org/Program/Details/39a4e769-abbf-467c-9af8-8f0163bfee37", isLiked: false))
        // MARK - 10
        internshipArray.append(Internship(title: "Center for the Advancement of Hispanics in Science and Engineering Education", company: "CASHEE", location: "Landover, MD", description: "The Center for the Advancement of Hispanics in Science and Engineering Education is a national educational and scientific non-profit organization based in Washington DC created by Latino scientists and engineers. Their mission is to prepare talented Hispanic and other underrepresented minority science and engineering students reach academic excellence and professional success through CAHSEE's rigorous education and leadership development programs.", url: "http://www.cahsee.org/", isLiked: false))
        internshipArray.append(Internship(title: "Diversity Programs", company: "Barclays", location: "Varies", description: "The diversity programs offers a wide range of career opportunities to help expand your learning. You'll find an overview of all our diversity programs below, so use our dropdown filters to find the ones that suit you best. Click 'Find out more' to learn more", url: "https://joinus.barclays/americas/diversity-programs/", isLiked: false))
        internshipArray.append(Internship(title: "ASPIRA Programs", company: "ASPIRA", location: "Varies", description: "ASPIRA has had over forty-nine years of experience creating and implementing  informal education programs that build up self-esteem, cultural awareness, and leadership abilities.", url: "http://www.aspira.org/book/programs", isLiked: false))
        internshipArray.append(Internship(title: "The HACU National Internship Program (HNIP)", company: "Hispanic Association of Colleges and Universities", location: "Varies", description: "The HACU National Internship Program partners with some of the leading corporations in the nation to help them hiring the best talent from HACU member colleges and universities. The process is very competitive and rewarding because many internships lead to full-time roles. Opportunities for both internships and full-time employment are available.", url: "https://www.hacu.net/hacu/Corporate_HACU_Internship_Program.asp", isLiked: false))
        internshipArray.append(Internship(title: "Coro Kansas City-Summer Internship in Public Affairs", company: "Coro", location: "Kansas City", description: "The Summer Internship in Public Affairs is a full-time, ten-week summer program, whose purpose is to develop future community leaders through exposure to community issues, skill development, civic leaders and organizations. The program is demanding, full-time, and requires an interest in public affairs. Each intern is assigned an individual internship in business, government, labor, media, or non-profit organizations. A preference in consideration is given to applicants from the Greater Kansas City area or to those who intend to include Kansas City in their future plans. Both undergraduate and graduate students are eligible to apply. Twelve interns are selected every summer. Interns receive compensation.", url: "http://www.coro.org", isLiked: false))
