        scholarshipArray.append(Scholarship(title: "¡Adelante! HOPE Alamo Community College District Scholarship", amount: "$1000", deadline: "March 18, 2019", description: "The ¡Adelante! U.S. Education Leadership Fund is a leadership development, non-profit organization dedicated to college students of Hispanic descent. Their mission is to inspire the best and brightest Latino students to graduate and lead through scholarships, internships, and leadership training. Applicants must have completed at least 30 credit hours of college coursework prior to the upcoming fall academic period and must have attended a San Antonio area high school and be a U.S. Citizen or permanent resident who is of Hispanic descent with a minimum 2.75 GPA. Must maintain full-time enrollment status (12 hours) for the duration of the scholarship period.", url: "https://www.adelantefund.org/#!scholarships/cee5", isLiked: false))
        // MARK - 5
        scholarshipArray.append(Scholarship(title: "ALLIES Scholarship for Student Leadership Development", amount: "$500", deadline: "March 15, 2019", description: "Applicants must be full-time students at Northern Michigan University in good academic standing and have shown leadership, support, or involvement in organizations, activities, or issues that promote awareness and acceptance of the LGBTQIA Northern Michigan University.", url: "http://www.nmu.edu/multiculturaledandres/node/9#__utma", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Audria M. Edwards Scholarship Fund", amount: "Varies", deadline: "May 01, 2019", description: "The Audria M. Edwards Scholarship Fund is for students who will be pursuing undergraduate education in an accredited institution or program during the coming school year and who are lesbian, gay, bisexual, or transgender or have a LGBT parent. Applicants must have resided for at least one year in the State of Oregon or in Clack, Cowlitz, Skamania, or Wahkiakum Counties in Washington.", url: "http://www.peacockinthepark.org/scholarship/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Better Brothers LA Book Scholarship", amount: "Varies", deadline: "January 19, 2019", description: "Better Brothers LA assists Black LGBTQ youth with avoiding financial hindrances imposed by school-related feed by offering scholarships. The scholarships support youth who have been admitted to or are attending an accredited degree, licensing or vocational program anywhere in the country.", url: "http://betterbrothersla.com/book-scholarship.shtml", isLiked: false))
        scholarshipArray.append(Scholarship(title: "CFPCA LGBT Endowed Scholarship", amount: "$500", deadline: "February 01, 2019", description: "The CFPCA LGBT Endowed Scholarship provides financial support and hope to students who demonstrate positive sensitivity to and involvement in LGBT issues. Preference is given to LGBT students although all students are invited to apply. All current undergraduate and graduate students must be enrolled at least half-time with majors in the College of Fine, Performing and Communication Arts who are in good academic standing may apply.", url: "http://cfpca.wayne.edu/scholarships.php", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Chely Wright Like Me Scholarship", amount: "$1250", deadline: "May 31, 2019", description: "The Chely Wright LIKE ME Scholarship provides financial support for students pursuing a college degree who actively advocate for LGBT youth and to students who have been affected by teen bullying or suicide of an LGBT youth. Through this scholarship, LGBT affected youth can apply for a chance to have their educational expenses paid and to pursue a degree from an accredited college, university, or technical/vocational program. Graduating high school seniors who can show that they have actively advocated for LGBT issues, such as respect and inclusion for LGBT youth, community service at an LGBT resource, involvement and/or attendance of LGBT events, working against teen bullying or teen suicide, and any other activities that improve the condition of the LGBT community and/or improve cultural conditions for LGBT youth are eligible to apply. For more information or to apply, please visit the scholarship provider's website.", url: "http://likeme.org/chely-wright-scholarship/", isLiked: false))
        // MARK - 10
        scholarshipArray.append(Scholarship(title: "Ann Seki Memorial Scholarship", amount: "Varies", deadline: "April 30, 2019", description: "This scholarship is sponsored by Chevron and is awarded in memory of Ann Seki, an original member of the Chevron Hispanic Recruiting Team. Ann Seki had a determined viewpoint in ensuring Chevron met diversity hiring goals and was instrumental in developing a process to identify and select Chevron employees for higher recognition outside Chevron. This scholarship has been awarded since 2010. Eligibility: Hispanic/Latino, be a freshmen, sophomore, or junior in college, major in Engineering (Civil, Chemical, Electrical, Facilities, Mechanical, and Petroleum), minimum 3.3 GPA, a U.S. Citizen/Permanent Resident", url: "http://www.greatmindsinstem.org/college/ann-seki-memorial-scholarship", isLiked: false))
        scholarshipArray.append(Scholarship(title: "APA Judith McManus Price Scholarship", amount: "$4000", deadline: "April 30, 2019", description: "Women and minority (African American, Hispanic American, or Native American) students enrolled in an approved Planning Accreditation Board planning program who are citizens of the United States, intend to pursue careers as practicing planners in the public sector, and are able to demonstrate a genuine financial need are eligible to apply for this scholarship.", url: "https://www.planning.org/foundation/scholarships/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "APSA Minority Fellowship Program", amount: "$4000", deadline: "October 30, 2018", description: "Minority Fellows Program is a fellowship competition for individuals from under-represented backgrounds applying to doctoral programs in political science. Eligibility: Must be a member of one of the following racial/ethnic minority groups: African Americans, Asian Pacific Americans, Latinos/as, and Native Americans, be a college/university seniors, college/university graduates, or students currently enrolled in a master's program, demonstrate an interest in teaching and potential for research in political science, be a U.S. citizen.", url: "https://www.apsanet.org/mfp", isLiked: false))
        scholarshipArray.append(Scholarship(title: "ASA Minority Fellowship Program", amount: "$18000", deadline: "January 31, 2019", description: "Minority Fellowship Program applicants can be new or continuing graduate students. However, the MFP is primarily designed for minority students entering a doctoral program in sociology for the first time or for those who are in the early stages of their graduate programs. MFP applicants must be applying to or enrolled in sociology departments which have strong mental health research programs and/or faculty who are currently engaged in research focusing on mental health issues. MFP fellows are selected on the basis of their commitment to research in mental health and mental illness, academic achievement, scholarship, writing ability, research potential, financial need, and racial/ethnic minority background. Specifically, applicants must be members of one of the following racial/ethnic groups: Blacks/African Americans, Latinos/as (e.g., Mexican Americans, Puerto Ricans, Cubans), American Indians or Alaskan Natives, and Asians (e.g., Chinese, Japanese, Korean, Southeast Asian), or Pacific Islanders (e.g., Hawaiian, Guamanian, Samoan, Filipino).", url: "http://www.asanet.org/career-center/grants-and-fellowships/minority-fellowship-program", isLiked: false))
        scholarshipArray.append(Scholarship(title: "ALA - LITA/LSSI Scholarship", amount: "$2500", deadline: "March 01, 2019", description: "ACS awards renewable scholarships to underrepresented minority students who want to enter the fields of chemistry or chemistry-related fields. African American, Hispanic, or American Indian high school seniors or college freshman, sophomores, or juniors pursuing a college degree in the chemical sciences or chemical technology are eligible to apply for this scholarship. To be considered for a scholarship through the ACS Scholars Program, candidates must: be African-American, Hispanic/Latino, or American Indian, be a U.S. citizen or permanent U.S. resident, be a full-time student at a high school or accredited college, university, or community college, demonstrate high academic achievement in chemistry or science with a 3.0+ GPA, demonstrate financial need according to the Free Application for Federal Student Aid form (FAFSA) and the Student Aid Report (SAR) form, be a graduating high school senior or college freshman, sophomore or junior intending to or already majoring in chemistry, biochemistry, chemical engineering or a chemically-related science OR intending to or already pursuing a degree in chemical technology, be planning a career in the chemical sciences. Please note that students intending to enter Pre-Med programs or pursuing a degree in Pharmacy are not eligible for this scholarship. For more information or to apply, please visit the scholarship provider's website.", url: "http://www.ala.org/lita/awards/lssi", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Augustana University Diversity Scholarships", amount: "Varies", deadline: "Varies", description: "Diversity Scholarships are for full-time students who are members of a minority ethnic group and bring diversity to campus. Ethnicity and financial need are two of the factors considered. Verify your eligibility with the Office of Admission or Financial Aid.", url: "http://www.augie.edu/admission/financing-your-education/scholarships", isLiked: false))
        // MARK - 15
        scholarshipArray.append(Scholarship(title: "Jesse L. Jackson-Fellows Toyota Scholarship", amount: "$20000", deadline: "May 01, 2019", description: "The Jesse Jackson Fellows-Toyota Scholarship is a renewable scholarship that awards up to $25,000 dollars annually to deserving African-American college sophomores. Students who are interested in applying for the scholarship must have a minimum cumulative 3.0 GPA, be a business or STEM (Science, technology, engineering and/or math) major that can be applied to the automotive industry, demonstrated participation in community service and need for financial assistance. For more information or to apply, please visit the scholarship provider's website.", url: "http://www.pushexcel.org/scholarships/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Jonathan LaRon Skinner Memorial Scholarship", amount: "$500", deadline: "April 01, 2019", description: "The Jonathan LaRon Skinner Memorial Scholarship was established by the family and friends of Jonathan Skinner in 2010 in memory of Jonathan LaRon Skinner to provide scholarships to graduating high school seniors. Applicants must attend a Forsyth County high school and reside in The Winston-Salem Foundation's service area with preference given to students attending Quality Education High School of Business and Entrepreneurship; have a minimum 3.0 GPA and be African American.", url: "https://www.wsfoundation.org/sslpage.aspx?pid=745", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Juanita Crippen Memorial Scholarship", amount: "$500", deadline: "April 30, 2019", description: "To recognize a senior high school student from a migrant farm-worker family in Franklin, St. Lawrence, or Clinton Counties in New York State needing post-secondary scholarship assistance who has demonstrated a caring and giving attitude toward another individual or community with a scholarship. Requirements: Two reference letters from a school representative (such as a counselor, teacher, coach, or other) addressing your educational commitment, grades, etc. and supporting the need for financial assistance. People writing the reference letter cannot be anyone under 21 years of age, or anyone related to you or serving as a legal guardian. A personal essay must be included (250-300 words) with information about what service to others means to you and how you have benefited by assisting someone else. What did you learn from the experience? Must provide proof of acceptance/enrollment at a college/university, proof of migrant education eligibility with a Certificate of Eligibility (COE) showing qualifications as a migrant student during the past three years or any time during high school.", url: "http://migrant.net/migrant/scholarships/crippen.htm", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Kahdine Ann DaCosta Scholarship for Excellence in Leadership", amount: "$1000", deadline: "March 01, 2019", description: "The Kahdine Ann DaCosta Memorial Scholarship for Excellence in Leadership was established on October 15, 2011 in honor of Kahdine Ann DaCosta. Every year, I AM O'KAH! searches for students that embody Kahdine’s tenacious drive and unwavering dedication to the community. Applicants must attend a high school in the Baltimore Metro area (Baltimore City, Baltimore County and Ann Arundel County) and be a senior in good standing, as defined by school and verified by official transcript. Applicants must have a minimum cumulative grade point average of 2.5 out of 4.0 or equivalent. Have demonstrated leadership abilities through their participation in community service, extracurricular activities, or other activities shown to foster leadership skills. A minimum of two consecutive years of community service/activities is required. Will enroll for the first time at an accredited two- or four-year college/university (including trade/vocational school where they will earn an associate degree) located in the United States in the upcoming academic year. Must demonstrate financial need which is verified through FAFSA.", url: "http://www.iamokah.org/scholarship/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Kevin W. Scott Memorial Scholarship", amount: "Varies", deadline: "February 15, 2019", description: "Kelvin W. Scott, an outstanding member of the basketball and track teams at Arthur Hill High School, set remaining track records before graduating in 1980. Kelvin continued on to be a Big Ten champion and record setter in indoor and outdoor track at Michigan State University in the 1 mile, 1,600 meter and the 4×400-meter relay teams in 1982 and 1983. After graduating from MSU in 1984, Kelvin went on to receive a Juris Doctorate degree from Georgetown University Law Center in 1987. An attorney in both corporate and governmental fields and a member of various legal organizations, Kelvin served as the director of the Michigan Department of Civil Rights at the time of his passing where he championed the rights of people without full equality in Michigan. Requirements: Graduating senior from Arthur Hill High School, Saginaw High School or Saginaw Arts & Sciences Academy, minimum 3.2 GPA, pursuing an undergraduate degree in pre-law.", url: "http://www.saginawfoundation.org/site/kelvin-w-scott-memorial-scholarship/", isLiked: false))
        // Mark - 20
        scholarshipArray.append(Scholarship(title: "Endowment for South Asian Students of Indian Descent Scholarship Fund", amount: "$2000", deadline: "September 30, 2018", description: "A $2,000 scholarship will be awarded to a qualified medical student. Eligibility: applicant must be a Pennsylvania resident for at least 12 months before registering as a medical student (not including time spent attending a Pennsylvania undergraduate or graduate school), must be a South Asian Indian or a descendant of a South Asian Indian immigrant to the United States; the following lineage qualifies: self, parent(s), grandparent(s), or great-grandparent(s) of South Asian Indian heritage, must be enrolled full-time in an accredited Pennsylvania medical school, must be enrolled or entering his/her second, third or fourth year of medical school, must have completed a FAFSA", url: "https://www.petersons.com/scholarship/endowment-for-south-asian-students-of-indian-descent-scholarship-111_166692.aspx", isLiked: false))
        scholarshipArray.append(Scholarship(title: "EPP Undergraduate Scholarship Program", amount: "$45000", deadline: "January 31, 2019", description: "The goal of the EPP Undergraduate Scholars Program is to increase the number of students who undertake course work and graduate with degrees in targeted academic fields integral to NOAA's mission. This program targets students who have completed their sophomore year, attending minority serving institutions, and have recently declared, or about to declare a major in atmospheric, oceanic, or environmental disciplines that support these sciences. Applicant must be a U.S. citizen currently enrolled or accepted as a full-time, second-year student in a four-year academic program or a third-year student in a five-year program in a discipline related to NOAA's programs and mission at an accredited minority-serving institution (college or university within the United States or U.S. territories). Applicants must earn and maintain a minimum 3.2 GPA.", url: "http://www.noaa.gov/office-education/epp-msi/undergraduate-scholarship", isLiked: false))
        scholarshipArray.append(Scholarship(title: "ESA Foundation Scholarship Program for Minority Students", amount: "$3000", deadline: "May 04, 2019", description: "The ESA Foundation scholarships are offered for full-time undergraduate study at accredited four-year colleges and universities in the USA. Up to 30 scholarships of $3,000 each will be awarded annually, 15 to graduating high school seniors and 15 to current college students. Women and minority students are eligible to apply, so long as they are U.S. citizens and have a minimum 2.75 GPA. Students must also be pursuing degrees leading to careers in computer and video game arts. For more information or to apply, please visit the scholarship provider's website.", url: "http://www.esafoundation.org/scholarship.asp", isLiked: false))
        scholarshipArray.append(Scholarship(title: "F.A. and Charlotte Blount Scholarship", amount: "$3000", deadline: "April 01, 2019", description: "The F.A. and Charlotte Blount Scholarship was established in 1997 to provide college scholarships to graduating Forsyth County high school seniors who will pursue a four-year baccalaureate degree at an accredited college or university. To receive consideration, applicants must be African-American or Hispanic, have a minimum 3.0 GPA and demonstrate financial need. For more information or to apply, please visit the scholarship provider's website.", url: "https://www.wsfoundation.org/document.doc?id=162", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Fontana Transport Inc. Scholars Program", amount: "$5000", deadline: "March 14, 2019", description: "The Fontana Transport Inc. Scholars Program is open to underrepresented, low-income and first-generation college-bound students. Students must be pursuing an undergraduate degree full-time in transportation, math, science, engineering, architecture, environmental design, PreMed, psychology, Spanish language/literature. The applicant does not have to be a U.S. citizen but must have a minimum 3.5 GPA and proof of attending college beginning in the fall semester. Recommendation by a faculty member is optional but highly suggested. For more information or to apply, please visit the scholarship provider's website.", url: "https://www.scholarships.com/financial-aid/fontana-transport-inc-scholars-program/", isLiked: false))
        // MARK - 25
        scholarshipArray.append(Scholarship(title: "Gay Asian Pacific Alliance Foundation Scholarship", amount: "$5000", deadline: "June 30, 2019", description: "The purpose of the Gay Asian Pacific Alliance (GAPA) Foundation Scholarship is to provide financial assistance to students in high school, undergraduate, graduate or professional school or trade or vocational school who express activism in the Asian and Pacific Islander (API) and/or lesbian, gay, bisexual, transgender, and queer (LGBTQ) communities. Applicants should have a strong history of activism within either the API and/or LGBTQ communities; however, applicants need not identify as either to apply. Preference is given to those whose identity sides with the API or LGBT society.", url: "https://gapafoundation.org/scholarships/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "George A. Strait Minority Scholarships", amount: "Varies", deadline: "Varies", description: "George A. Strait Minority Scholarships are awarded to college graduates with meaningful law library experience who are members of a minority group as defined by current U.S. government guidelines, are degree candidates in an accredited library or law school, and who intend to have a career in law librarianship. Applicants must show evidence of financial need. To apply for the George A. Strait Minority Scholarship, you must also qualify for one of the AALL Educational Scholarships.", url: "https://www.aallnet.org/education-training/scholarships/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "GRCF Arts Council of Greater Grand Rapids Minority Scholarship", amount: "Varies", deadline: "April 01, 2019", description: "The Arts Council of Greater Grand Rapids Minority Scholarship is open to students of color (African-American, Asian, Hispanic, Native American, Pacific Islander) attending a non-profit public or private college/university majoring in fine arts. Applicants demonstrate financial need, be a Kent County resident, and have a minimum 2.5 GPA.", url: "https://www.petersons.com/scholarship/Arts-Council-of-Greater-Grand-Rapids-Minority-Scholarship-111_189180.aspx", isLiked: false))
        scholarshipArray.append(Scholarship(title: "GRCF Hackett Family Scholarship", amount: "Varies", deadline: "April 01, 2019", description: "Grand Rapids Community Foundation awards hundreds of scholarships annually, primarily to Kent County students bound for or in college, or those pursuing a technical career. To be eligible for the Hackett Family Scholarship, students must be seniors at, or have graduated from any Grand Rapids public high school and is an undergraduate attending an accredited college/university or skilled trade school of their choice. Preference is given to women of color. For more information or to apply, please visit the scholarship provider's website.", url: "https://www.grfoundation.org/apply-for-scholarships", isLiked: false))
        scholarshipArray.append(Scholarship(title: "GRCF Miller Johnson West Michigan Diversity Law School Scholarship", amount: "$5000", deadline: "April 01, 2019", description: "The Grand Rapids Community Foundation administers and awards the following Law Scholarships to students of color (African American, Asian, Hispanic, Native American, Pacific Islander). The Miller Johnson West Michigan Diversity Law School Scholarship is awarded to students of color currently residing in Michigan (with preference given to West Michigan residents) pursuing a law degree at any accredited law school in the United States. Students must demonstrate financial need and have a minimum 3.0 cumulative GPA.", url: "https://www.affordablecollegesonline.org/college-resource-center/scholarships-for-asian-and-pacific-islander-students/", isLiked: false))
        // MARK - 30
        scholarshipArray.append(Scholarship(title: "GE/LNESC Scholarship", amount: "$5000", deadline: "Varies", description: "The partnership between the General Electric Foundation and the LNESC Scholarship Program is intended to assist and encourage outstanding minority students in completing their college education. The program is directed specifically to disadvantaged minority students with career interests in business or engineering. Eligibility: be a minority student pursuing full-time studies leading to a bachelor's degree at an accredited college or university in the U.S., classified as a college, have a cumulative college grade point average of at least 3.25 on a 4.0 scale or the equivalent, a business or engineering major, a U.S. citizen or legal resident", url: "https://www.lnesc.org/gelnesc-scholarship", isLiked: false))
        scholarshipArray.append(Scholarship(title: "GE-NMF Primary Care Leadership Program (PCLP)", amount: "$5000", deadline: "January 31, 2019", description: "The GE-NMF Primary Care Leadership Program  is a six-week summer program that includes clinical experience, leadership training, and a service-learning project that focuses on barriers in healthcare. PCLP provides students with an opportunity to examine the challenges and rewards of primary care practice at community health centers (CHCs) across the United States. Medical students and graduate nursing and physician assistant students from underrepresented minorities or socioeconomically disadvantaged backgrounds are encouraged to apply.", url: "https://nmfonline.org/about-our-scholarships-and-awards/service-learning-programs/nmf-primary-care-leadership-program/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "GRCF Arts Council of Greater Grand Rapids Minority Scholarship", amount: "Varies", deadline: "April 01, 2019", description: "The Arts Council of Greater Grand Rapids Minority Scholarship is open to students of color (African-American, Asian, Hispanic, Native American, Pacific Islander) attending a non-profit public or private college/university majoring in fine arts, including all visual and performing arts. Applicants must have financial need, be a Kent County resident, and have a minimum 2.5 GPA.", url: "https://www.petersons.com/scholarship/Arts-Council-of-Greater-Grand-Rapids-Minority-Scholarship-111_189180.aspx", isLiked: false))
        scholarshipArray.append(Scholarship(title: "George A. Strait Minority Scholarships", amount: "Varies", deadline: "Varies", description: "George A. Strait Minority Scholarships are awarded annually to college graduates with meaningful law library experience who are members of a minority group as defined by current U.S. government guidelines, are degree candidates in an accredited library or law school, and who intend to have a career in law librarianship. Applicants must show evidence of financial need. To apply for the George A. Strait Minority Scholarship, you must also qualify for one of the AALL Educational Scholarships.", url: "https://www.unigo.com/scholarships/all/George-A.-Strait-Minority-Scholarship/1004039", isLiked: false))
        scholarshipArray.append(Scholarship(title: "George Scott Jr. Memorial Scholarship", amount: "Varies", deadline: "May 15, 2019", description: "The George Scott Jr. Memorial Scholarship was established by Mr. Kent Hance in memory of his good friend George Scott Jr. to provide scholarships to students to attend Texas Tech University. Preference shall be given to academically-motivated African American students from Lubbock, Texas who will be attending Texas Tech University.", url: "https://www.cfwtx.org/current-scholarships", isLiked: false))
        // MARK - 35
        scholarshipArray.append(Scholarship(title: "Girls Who Illustrate Awesomeness Scholarship", amount: "$750", deadline: "Varies", description: "This is a scholarship opportunity for young women of color who are actively working to change the world. Applicants must be young women of color who are currently enrolled in college/university or will begin college/university in the spring semester and have a minimum of 3.0 GPA.", url: "http://simpli.works/scholarship/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "GRCF Warner Norcross & Judd Paralegal and Legal Secretarial Scholarship", amount: "$2000", deadline: "April 01, 2019", description: "The Grand Rapids Community Foundation administers and awards the following Law Scholarships to students of color (African American, Asian, Hispanic, Native American, Pacific Islander). The Warner Norcross & Judd LLP Paralegal or Secretarial Scholarship is awarded to students of color residing in Michigan pursuing paralegal or legal secretarial studies at an accredited program located in Michigan", url: "https://www.scholarships.com/financial-aid/college-scholarships/scholarships-by-type/minority-scholarships/latino-scholarships/grcf-warner-norcross-and-judd-paralegal-and-legal-secretarial-scholarship/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Gwenn Steward Clements Scholarship", amount: "$1000", deadline: "April 01, 2019", description: "The Gwenn Steward Clements Scholarship provides scholarships to graduating high school seniors from Lexington Senior High School in Davidson County who possess outstanding oral and written skills and who will pursue undergraduate degrees from accredited post-secondary institutions. Students must have a minimum 3.25 GPA who is solid in English, possessing outstanding oral and written skills along with an A- average. Preference will be given to minority students, particularly African-Americans.", url: "https://www.wsfoundation.org/document.doc?id=291", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Herman J. Neal Scholarship", amount: "$4000", deadline: "April 01, 2019", description: "The Herman J. Neal scholarship supports African-American students in their pursuit of an accounting education and the CPA designation. These scholarships support students with their junior or senior year, or with the additional year, needed to sit for the CPA Exam which can include graduate studies. Scholarships cover the costs of tuition, fees, and can include up to $500 in expenses for books and required classroom materials. They do not cover costs of CPA Exam review courses. A minimum 3.0 GPA is required. Students must plan to sit for the CPA exam in Illinois within three years of the application date and demonstrate progress in accounting courses.", url: "https://findyourscholarship.com/illinois-cpa-society-herman-j-neal-scholarship/", isLiked: false))
        scholarshipArray.append(Scholarship(title: "Historically Underrepresented Groups Scholarship (HUGS)", amount: "$5000", deadline: "January 29, 2019", description: "The SAA is committed to assisting individuals in realizing their goals of entering careers in archaeology by offering two categories of scholarships: the HUGS and the HUGS-IFR. The SAA Historically Underrepresented Groups Scholarship (HUGS) helps increase the number of under-represented minorities obtaining degrees in archaeology. It provides funding to minority archaeology students, helping them enhance their education and successfully prepare for a variety of careers in archaeology and heritage management. The scholarship is overseen by the Minority Scholarships Committee of the SAA.", url: "http://saa.org/AbouttheSociety/Awards/HistoricallyUnderrepresentedGroupsScholarship/tabid/1516/Default.aspx", isLiked: false))
